import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import * as schemeService from '../../services/schemes';
import LoadingSpinner from '../common/LoadingSpinner';
import '../admin/UserManagement.css'; // Re-using admin table styles

const CoordinatorSchemeManagement = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchSchemes();
  }, []);

  const fetchSchemes = async () => {
    try {
      setLoading(true);
      const data = await schemeService.getAllSchemes();
      setSchemes(data);
    } catch (err) {
      setError(err.toString());
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="user-management-container"> {/* Re-using container style */ }
      <h2>Scheme Management</h2>
      {error && <p className="dashboard-error">Error: {error}</p>}
      
      <table className="dashboard-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Type</th>
            <th>Department</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {schemes.map((scheme) => (
            <tr key={scheme._id}>
              <td>{scheme.title}</td>
              <td>
                <span className={`status-badge status-${scheme.schemeType.toLowerCase()}`}>
                  {scheme.schemeType}
                </span>
              </td>
              <td>{scheme.department}</td>
              <td className="actions-cell">
                {/* Coordinators can only view, not edit */}
                <Link to={`/schemes/${scheme._id}`} className="btn-review">
                  View
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CoordinatorSchemeManagement;
