import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import * as schemeService from '../../services/schemes';
import * as adminService from '../../services/admin';
import LoadingSpinner from '../common/LoadingSpinner';
import Pagination from '../common/Pagination'; // <-- 1. IMPORT PAGINATION
import './UserManagement.css'; // Re-using admin table styles

const SchemeManagement = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // --- 2. ADD PAGINATION STATE ---
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // --- 3. UPDATE useEffect DEPENDENCIES ---
  useEffect(() => {
    fetchSchemes();
  }, [page]); // Re-run when 'page' changes

  const fetchSchemes = async () => {
    try {
      setLoading(true);
      // --- 4. UPDATE API CALL TO PASS THE PAGE NUMBER ---
      // We don't pass filters, so we send an empty object {}
      const data = await schemeService.getAllSchemes({}, page); 
      
      // --- 5. SET STATE FROM THE NEW OBJECT ---
      setSchemes(data.schemes);
      setPage(data.page);
      setTotalPages(data.pages);
    } catch (err) {
      setError(err.toString());
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this scheme?')) {
      try {
        await adminService.deleteScheme(id);
        fetchSchemes(); // Refresh list
      } catch (err) {
        alert('Failed to delete scheme: ' + err.toString());
      }
    }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="user-management-container">
      <h2>Scheme Management</h2>
      {error && <p className="dashboard-error">Error: {error}</p>}
      
      <table className="dashboard-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Type</th>
            <th>Department</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {schemes.map((scheme) => ( // This line will no longer crash
            <tr key={scheme._id}>
              <td>{scheme.title}</td>
              <td>
                <span className={`status-badge status-${scheme.schemeType.toLowerCase()}`}>
                  {scheme.schemeType}
                </span>
              </td>
              <td>{scheme.department}</td>
              <td className="actions-cell">
                <Link to={`../schemes/edit/${scheme._id}`} className="btn-edit">
                  Edit
                </Link>
                <button 
                  className="btn-delete"
                  onClick={() => handleDelete(scheme._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      
      {/* --- 6. ADD THE PAGINATION COMPONENT --- */}
      <Pagination 
        page={page} 
        pages={totalPages} 
        onPageChange={(newPage) => setPage(newPage)}
      />
    </div>
  );
};

export default SchemeManagement;
