import React, { useState, useEffect } from 'react';
import * as schemeService from '../services/schemes';
import SchemeList from '../components/citizen/SchemeList';
import LoadingSpinner from '../components/common/LoadingSpinner';
import './Schemes.css';

const Schemes = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        setLoading(true);
        setError('');
        const data = await schemeService.getAllSchemes();
        setSchemes(data);
      } catch (err) {
        setError(err.toString());
      } finally {
        setLoading(false);
      }
    };

    fetchSchemes();
  }, []);

  return (
    <div className="schemes-container">
      <h1>All Schemes</h1>
      <p>Browse all available government and private schemes.</p>
      
      {loading && <LoadingSpinner />}
      {error && <div className="schemes-error">Error: {error}</div>}
      {!loading && !error && <SchemeList schemes={schemes} />}
    </div>
  );
};

export default Schemes;
