import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import * as userService from '../../services/user';
import AuthContext from '../../context/AuthContext';
import './SchemeCard.css';

// --- UPDATED: Component now accepts 'savedSchemeId' and 'onRemove' ---
const SchemeCard = ({ scheme, savedSchemeId, onRemove }) => {
  const { _id, title, description, department, schemeType } = scheme;
  const { user } = useContext(AuthContext);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState('');

  const truncateDescription = (text, length) => {
    if (text.length <= length) return text;
    return text.substring(0, length) + '...';
  };

  // --- NEW: Function to handle saving a scheme ---
  const handleSave = async () => {
    if (!user) {
      alert('Please log in to save schemes.');
      return;
    }
    setIsSaving(true);
    setError('');
    try {
      await userService.saveScheme(_id);
      alert('Scheme saved!');
    } catch (err) {
      setError(err.toString());
      alert('Error saving scheme: ' + err.toString());
    } finally {
      setIsSaving(false);
    }
  };

  // --- NEW: Function to handle unsaving a scheme ---
  const handleUnsave = async () => {
    if (!savedSchemeId) return;
    setIsSaving(true);
    try {
      await userService.removeSavedScheme(savedSchemeId);
      if (onRemove) {
        onRemove(savedSchemeId); // Tell the parent page to remove this card
      }
    } catch (err) {
      setError(err.toString());
      alert('Error removing scheme: ' + err.toString());
    } finally {
      setIsSaving(false);
    }
  };

  // --- NEW: Renders the correct button (Save or Unsave) ---
  const renderSaveButton = () => {
    if (!user || user.role !== 'Citizen') {
      return null; // Don't show save button if not a citizen
    }

    if (savedSchemeId) {
      // This is on the "Saved Schemes" page
      return (
        <button onClick={handleUnsave} className="card-btn-save unsave" disabled={isSaving}>
          {isSaving ? '...' : 'Unsave'}
        </button>
      );
    } else {
      // This is on the "All Schemes" page
      return (
        <button onClick={handleSave} className="card-btn-save save" disabled={isSaving}>
          {isSaving ? '...' : 'Save'}
        </button>
      );
    }
  };

  return (
    <div className="scheme-card">
      <div className="card-header">
        <span className={`card-badge ${schemeType.toLowerCase()}`}>
          {schemeType}
        </span>
        <h3 className="card-title">{title}</h3>
        <p className="card-department">{department}</p>
      </div>
      <div className="card-body">
        <p className="card-description">{truncateDescription(description, 100)}</p>
      </div>
      <div className="card-footer">
        {renderSaveButton()} {/* --- NEW BUTTON ADDED --- */}
        <Link to={`/schemes/${_id}`} className="card-link">
          View Details
        </Link>
      </div>
    </div>
  );
};

export default SchemeCard;
