import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import * as schemeService from '../services/schemes';
import * as appService from '../services/applications';
import * as userService from '../services/user'; // --- Import user service ---
import AuthContext from '../context/AuthContext';
import LoadingSpinner from '../components/common/LoadingSpinner';
import './SchemeDetails.css';

const SchemeDetails = () => {
  const [scheme, setScheme] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [selectedFiles, setSelectedFiles] = useState(null); 
  const [applyLoading, setApplyLoading] = useState(false);
  
  // --- NEW: State to track if user is re-submitting ---
  const [existingApplication, setExistingApplication] = useState(null);
  
  const { id } = useParams();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSchemeAndStatus = async () => {
      try {
        setLoading(true);
        // 1. Get scheme details
        const schemeData = await schemeService.getSchemeById(id);
        setScheme(schemeData);

        // 2. If user is logged in, check if they have an existing application
        if (user) {
          const myApps = await appService.getMyApplications();
          const app = myApps.find(a => a.scheme._id === id);
          if (app) {
            setExistingApplication(app);
            if(app.status === 'More Info Required') {
              setError(`Coordinator's Notes: ${app.coordinatorNotes}. Please re-upload documents and submit again.`);
            }
          }
        }
      } catch (err) {
        setError(err.toString());
      } finally {
        setLoading(false);
      }
    };
    fetchSchemeAndStatus();
  }, [id, user]); // Re-run if user logs in

  const handleFileChange = (e) => {
    setSelectedFiles(e.target.files);
  };

  const handleApply = async () => {
    if (!user) {
      navigate('/login');
      return;
    }
    
    // Check if documents are required but not provided
    // On re-submit, new documents are optional (they might just be fixing form data)
    if (scheme.documentsRequired.length > 0 && !selectedFiles && !existingApplication) {
       setError('This scheme requires documents to be uploaded. Please select files.');
       return;
    }

    setApplyLoading(true);
    setError('');
    setMessage('');

    const formData = new FormData();
    formData.append('schemeId', scheme._id);

    // Use profile data for the formData field
    const profileFormData = {
      fullName: user.profile?.firstName || 'Demo User',
      email: user.email,
      age: user.profile?.age || 'N/A',
      state: user.profile?.state || 'N/A',
    };
    formData.append('formData', JSON.stringify(profileFormData));
    
    if (selectedFiles) {
      for (let i = 0; i < selectedFiles.length; i++) {
        formData.append('documents', selectedFiles[i]);
      }
    }
    
    try {
      await appService.submitApplication(formData);
      setMessage('Application submitted successfully! Check "My Applications" for status.');
      // After submitting, clear the "More Info" status
      setExistingApplication(null); 
    } catch (err) {
      setError(err.message);
    } finally {
      setApplyLoading(false); 
    }
  };

  const renderApplyButton = () => {
    if (scheme.schemeType === 'Government') {
      return (
        <a href={scheme.officialLink} target="_blank" rel="noopener noreferrer" className="btn-apply government">
          Apply on Official Website
        </a>
      );
    }
    
    if (!user) {
      return (
        <button onClick={() => navigate('/login')} className="btn-apply private">
          Login to Apply
        </button>
      );
    }

    if (user.role !== 'Citizen') {
      return null;
    }

    // --- UPDATED: Check existing application status ---
    let buttonText = 'Submit Application';
    let isButtonDisabled = applyLoading;
    
    if (existingApplication) {
      if (existingApplication.status === 'More Info Required') {
        buttonText = 'Re-submit Application';
      } else if (existingApplication.status === 'Pending') {
        buttonText = 'Submitted (Pending)';
        isButtonDisabled = true;
      } else {
        buttonText = `Status: ${existingApplication.status}`;
        isButtonDisabled = true;
      }
    }

    return (
      <div className="apply-form">
        <div className="form-group">
          <label htmlFor="documents">Upload Documents (PDF, PNG, JPG)</label>
          <input 
            type="file" 
            id="documents" 
            name="documents" 
            multiple 
            onChange={handleFileChange} 
            className="file-input"
            disabled={isButtonDisabled} // Disable file input if already submitted
          />
          {scheme.documentsRequired.length > 0 && (
            <small>Required: {scheme.documentsRequired.join(', ')}</small>
          )}
        </div>
        <button 
          onClick={handleApply} 
          className="btn-apply private" 
          disabled={isButtonDisabled}
        >
          {applyLoading ? 'Submitting...' : buttonText}
        </button>
      </div>
    );
  };

  if (loading) return <LoadingSpinner />;
  // Show scheme details even if there's an application error
  if (!scheme) return <p>Scheme not found.</p>;

  return (
    <div className="scheme-details-container">
      <header className="scheme-header">
        <h1>{scheme.title}</h1>
        <p className="department">Department: {scheme.department}</p>
        <span className={`badge ${scheme.schemeType?.toLowerCase()}`}>{scheme.schemeType}</span>
      </header>
      
      <div className="scheme-content">
        <div className="scheme-main">
          <h3>Description</h3>
          <p>{scheme.description}</p>
          <h3>Benefits</h3>
          <ul>
            {scheme.benefits?.map((benefit, index) => (
              <li key={index}>{benefit}</li>
            ))}
          </ul>
          <h3>How to Apply</h3>
          <p>{scheme.howToApply || 'Follow the instructions on the official website.'}</p>
        </div>

        <aside className="scheme-sidebar">
          <div className="sidebar-card">
            <h3>Eligibility</h3>
            <ul>
              {scheme.eligibility?.ageMin && <li>Minimum Age: {scheme.eligibility.ageMin}</li>}
              {scheme.eligibility?.ageMax && <li>Maximum Age: {scheme.eligibility.ageMax}</li>}
              {scheme.eligibility?.state && <li>State: {scheme.eligibility.state}</li>}
              {scheme.eligibility?.gender && <li>Gender: {scheme.eligibility.gender}</li>}
              {scheme.eligibility?.annualIncomeMax && <li>Max Income: Rs. {scheme.eligibility.annualIncomeMax}</li>}
              {scheme.eligibility?.casteCategory && <li>Category: {scheme.eligibility.casteCategory}</li>}
            </ul>
          </div>
          <div className="sidebar-card">
            <h3>Documents Required</h3>
            {scheme.documentsRequired?.length > 0 ? (
              <ul>
                {scheme.documentsRequired.map((doc, index) => (
                  <li key={index}>{doc}</li>
                ))}
              </ul>
            ) : <p>No documents required.</p>}
          </div>
          <div className="apply-section">
            {/* Display errors/messages above the button */}
            {error && <div className="message error-message">{error}</div>}
            {message && <div className="message success-message">{message}</div>}
            {renderApplyButton()}
          </div>
        </aside>
      </div>
    </div>
  );
};

export default SchemeDetails;
