import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link
import * as appService from '../services/applications';
import LoadingSpinner from '../components/common/LoadingSpinner';
import './ApplicationStatus.css';

const ApplicationStatus = () => {
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchApplications = async () => {
      try {
        setLoading(true);
        const data = await appService.getMyApplications();
        setApplications(data);
      } catch (err) {
        setError(err.toString());
      } finally {
        setLoading(false);
      }
    };

    fetchApplications();
  }, []);

  if (loading) return <LoadingSpinner />;

  return (
    <div className="app-status-container">
      <h2>My Applications</h2>
      {error && <p className="error-text">{error}</p>}
      
      {applications.length === 0 && !error && (
        <p>You have not submitted any private applications yet.</p>
      )}

      {applications.length > 0 && (
        <table className="app-status-table">
          <thead>
            <tr>
              <th>Scheme</th>
              <th>Status</th>
              <th>Coordinator Notes</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {applications.map((app) => (
              <tr key={app._id}>
                <td>{app.scheme.title}</td>
                <td>
                  <span className={`status-badge status-${app.status.toLowerCase().replace(' ', '-')}`}>
                    {app.status}
                  </span>
                </td>
                <td className="notes-cell">
                  {app.coordinatorNotes || 'N/A'}
                </td>
                <td className="action-cell">
                  {/* --- NEW: Add Re-submit Button --- */}
                  {app.status === 'More Info Required' && (
                    <Link 
                      to={`/schemes/${app.scheme._id}`} 
                      className="btn-resubmit"
                    >
                      Re-submit
                    </Link>
                  )}
                  {/* --- END OF NEW --- */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ApplicationStatus;
