import api from './api';

// Submit a new application
export const submitApplication = async (schemeId, formData) => {
  try {
    const { data } = await api.post('/applications', { schemeId, formData });
    return data;
  } catch (error) {
    throw error.response.data.msg || 'Application submission failed';
  }
};

// Get all applications for the logged-in user
export const getMyApplications = async () => {
  try {
    const { data } = await api.get('/applications/my');
    return data;
  } catch (error) {
    throw error.response.data.msg || 'Failed to fetch applications';
  }
};
