import React, { useState, useEffect } from 'react'; // useContext was removed
import { useParams, useNavigate } from 'react-router-dom';
import * as coordService from '../../services/coordinator';
// import AuthContext from '../../context/AuthContext'; // This line was removed
import LoadingSpinner from '../common/LoadingSpinner';
import './ApplicationReview.css';

const ApplicationReview = () => {
  const [application, setApplication] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [notes, setNotes] = useState('');
  const [actionLoading, setActionLoading] = useState(false);
  
  const { id } = useParams();
  const navigate = useNavigate();
  // const { user } = useContext(AuthContext); // This line was removed

  useEffect(() => {
    const fetchApp = async () => {
      try {
        setLoading(true);
        const data = await coordService.getApplicationById(id);
        setApplication(data);
        if (data.status !== 'Pending') {
          setMessage(`This application was already ${data.status.toLowerCase()}.`);
          setNotes(data.coordinatorNotes || '');
        }
      } catch (err) {
        setError(err.toString());
      } finally {
        setLoading(false);
      }
    };
    fetchApp();
  }, [id]);

  const handleSubmit = async (status) => {
    setActionLoading(true);
    setError('');
    try {
      await coordService.updateApplicationStatus(id, status, notes);
      setMessage(`Application successfully ${status.toLowerCase()}!`);
      navigate('/coordinator-dashboard/pending');
    } catch (err) {
      setError(err.toString());
    } finally {
      setActionLoading(false);
    }
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <p className="dashboard-error">Error: {error}</p>;
  if (!application) return <p>Application not found.</p>;

  const { citizen, scheme, formData, status, documents } = application;
  const isPending = status === 'Pending';

  return (
    <div className="review-container">
      <h2>Review Application: {scheme.title}</h2>
      
      {message && <div className="review-message success">{message}</div>}
      
      <div className="review-grid">
        <div className="applicant-details">
          <h3>Applicant Details</h3>
          <p><strong>Username:</strong> {citizen.username}</p>
          <p><strong>Email:</strong> {citizen.email}</p>
          <p><strong>Age:</strong> {citizen.profile.age || 'N/A'}</p>
          <p><strong>State:</strong> {citizen.profile.state || 'N/A'}</p>
        </div>
        
        <div className="application-data">
          <h3>Application Form Data</h3>
          <ul>
            {Object.entries(formData).map(([key, value]) => (
              <li key={key}>
                <strong>{key}:</strong> {value}
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="document-list">
        <h3>Uploaded Documents</h3>
        {documents && documents.length > 0 ? (
          <ul>
            {documents.map((docPath, index) => (
              <li key={index}>
                <a 
                  href={`http://localhost:5001${docPath}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  Document {index + 1} ({docPath.split('-').pop()})
                </a>
              </li>
            ))}
          </ul>
        ) : (
          <p>No documents were uploaded with this application.</p>
        )}
      </div>

      <div className="review-action-box">
        <h3>Coordinator Action</h3>
        <div className="form-group">
          <label htmlFor="notes">Notes (optional for approval, required for rejection)</label>
          <textarea
            id="notes"
            rows="4"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            placeholder="Provide feedback to the citizen..."
            disabled={!isPending}
          ></textarea>
        </div>
        
        {isPending ? (
          <div className="action-buttons">
            <button
              className="btn-approve"
              onClick={() => handleSubmit('Approved')}
              disabled={actionLoading}
            >
              {actionLoading ? 'Loading...' : 'Approve'}
            </button>
            <button
              className="btn-reject"
              onClick={() => handleSubmit('Rejected')}
              disabled={actionLoading || !notes} 
            >
              {actionLoading ? 'Loading...' : 'Reject'}
            </button>
          </div>
        ) : (
          <p><strong>This application has already been reviewed.</strong></p>
        )}
      </div>
    </div>
  );
};

export default ApplicationReview;
