import React, { useState, useEffect, useContext } from 'react';
import AuthContext from '../context/AuthContext';
import * as userService from '../services/user';
import LoadingSpinner from '../components/common/LoadingSpinner';
import './Profile.css';

const Profile = () => {
  const { user, loading: authLoading } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    age: '',
    state: '',
    gender: '',
    occupation: '',
    annualIncome: '',
    casteCategory: '',
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (user && user.profile) {
      setFormData({
        firstName: user.profile.firstName || '',
        lastName: user.profile.lastName || '',
        age: user.profile.age || '',
        state: user.profile.state || '',
        gender: user.profile.gender || '',
        occupation: user.profile.occupation || '',
        annualIncome: user.profile.annualIncome || '',
        casteCategory: user.profile.casteCategory || '',
      });
    }
  }, [user]);

  const {
    firstName,
    lastName,
    age,
    state,
    gender,
    occupation,
    annualIncome,
    casteCategory,
  } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setError('');
    try {
      await userService.updateMyProfile(formData);
      setMessage('Profile updated successfully! You may need to refresh to see changes in the app.');
      // Note: To make this seamless, AuthContext should have a 'reloadUser' function
    } catch (err) {
      setError(err.toString());
    } finally {
      setLoading(false);
    }
  };

  if (authLoading || !user) return <LoadingSpinner />;

  return (
    <div className="profile-container">
      <h2>My Profile</h2>
      <p>Keep your profile updated. This information is used for checking scheme eligibility.</p>
      
      {message && <div className="profile-message success">{message}</div>}
      {error && <div className="profile-message error">{error}</div>}

      <form className="profile-form" onSubmit={onSubmit}>
        {/* --- Personal Info --- */}
        <fieldset>
          <legend>Personal Information</legend>
          <div className="form-group">
            <label htmlFor="firstName">First Name</label>
            <input type="text" name="firstName" value={firstName} onChange={onChange} />
          </div>
          <div className="form-group">
            <label htmlFor="lastName">Last Name</label>
            <input type="text" name="lastName" value={lastName} onChange={onChange} />
          </div>
          <div className="form-group">
            <label htmlFor="age">Age *</label>
            <input type="number" name="age" value={age} onChange={onChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="gender">Gender *</label>
            <select name="gender" value={gender} onChange={onChange} required>
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="state">State *</label>
            <input type="text" name="state" value={state} onChange={onChange} required placeholder="e.g., Tamil Nadu" />
          </div>
        </fieldset>

        {/* --- Socio-Economic Info --- */}
        <fieldset>
          <legend>Socio-Economic Information</legend>
          <div className="form-group">
            <label htmlFor="occupation">Occupation</label>
            <select name="occupation" value={occupation} onChange={onChange}>
              <option value="">Select Occupation</option>
              <option value="Student">Student</option>
              <option value="Salaried">Salaried</option>
              <option value="Self-Employed">Self-Employed</option>
              <option value="Unemployed">Unemployed</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="annualIncome">Annual Income (in Rs.)</label>
            <input type="number" name="annualIncome" value={annualIncome} onChange={onChange} placeholder="e.g., 500000" />
          </div>
          <div className="form-group">
            <label htmlFor="casteCategory">Category</label>
            <select name="casteCategory" value={casteCategory} onChange={onChange}>
              <option value="">Select Category</option>
              <option value="General">General</option>
              <option value="OBC">OBC</option>
              <option value="SC">SC</option>
              <option value="ST">ST</option>
              <option value="Other">Other</option>
            </select>
          </div>
        </fieldset>

        <button type="submit" className="profile-button" disabled={loading}>
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
      </form>
    </div>
  );
};

export default Profile;
