import React, { useState, useEffect } from 'react';
import * as userService from '../services/user';
import SavedScheme from '../components/citizen/SavedScheme'; // <-- Use the component
import LoadingSpinner from '../components/common/LoadingSpinner';
import './Schemes.css'; // Re-use styles

const SavedSchemes = () => {
  const [savedSchemes, setSavedSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSaved = async () => {
      try {
        setLoading(true);
        const data = await userService.getSavedSchemes();
        // data is an array of SavedScheme objects, each with a 'scheme' property
        setSavedSchemes(data);
      } catch (err) {
        setError(err.toString());
      } finally {
        setLoading(false);
      }
    };
    fetchSaved();
  }, []);
  
  return (
    <div className="schemes-container">
      <h1>My Saved Schemes</h1>
      {loading && <LoadingSpinner />}
      {error && <p className="schemes-error">Error: {error}</p>}
      {!loading && !error && <SavedScheme savedSchemes={savedSchemes} />}
    </div>
  );
};

export default SavedSchemes;
