import React, { useState, useEffect } from 'react';
import * as schemeService from '../services/schemes';
import SchemeList from '../components/citizen/SchemeList';
import LoadingSpinner from '../components/common/LoadingSpinner';
import './Schemes.css';

const Schemes = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // --- NEW: State for filters ---
  const [filters, setFilters] = useState({
    search: '',
    state: '',
    category: '',
    occupation: '',
  });

  // --- UPDATED: useEffect to re-fetch when filters change ---
  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        setLoading(true);
        setError('');
        // Pass the current filters to the API service
        const data = await schemeService.getAllSchemes(filters);
        setSchemes(data);
      } catch (err) {
        setError(err.toString());
      } finally {
        setLoading(false);
      }
    };

    fetchSchemes();
  }, [filters]); // Re-run this effect whenever the 'filters' object changes

  // --- NEW: Handler to update filter state ---
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prevFilters) => ({
      ...prevFilters,
      [name]: value,
    }));
  };

  return (
    <div className="schemes-container">
      <h1>All Schemes</h1>
      <p>Browse all available government and private schemes.</p>
      
      {/* --- NEW: Filter Bar --- */}
      <div className="filter-bar">
        <input
          type="text"
          name="search"
          placeholder="Search by title..."
          value={filters.search}
          onChange={handleFilterChange}
          className="filter-search"
        />
        <select
          name="state"
          value={filters.state}
          onChange={handleFilterChange}
          className="filter-select"
        >
          <option value="">All States</option>
          <option value="Tamil Nadu">Tamil Nadu</option>
          <option value="Kerala">Kerala</option>
          <option value="Karnataka">Karnataka</option>
          <option value="Maharashtra">Maharashtra</option>
          <option value="Delhi">Delhi</option>
          <option value="West Bengal">West Bengal</option>
          <option value="Rajasthan">Rajasthan</option>
        </select>
        <select
          name="category"
          value={filters.category}
          onChange={handleFilterChange}
          className="filter-select"
        >
          <option value="">All Categories</option>
          <option value="General">General</option>
          <option value="OBC">OBC</option>
          <option value="SC">SC</option>
          <option value="ST">ST</option>
        </select>
        <select
          name="occupation"
          value={filters.occupation}
          onChange={handleFilterChange}
          className="filter-select"
        >
          <option value="">All Occupations</option>
          <option value="Student">Student</option>
          <option value="Salaried">Salaried</option>
          <option value="Self-Employed">Self-Employed</option>
          <option value="Unemployed">Unemployed</option>
        </select>
      </div>
      {/* --- END: Filter Bar --- */}

      {loading && <LoadingSpinner />}
      {error && <div className="schemes-error">Error: {error}</div>}
      {!loading && !error && <SchemeList schemes={schemes} />}
    </div>
  );
};

export default Schemes;
