import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import * as schemeService from '../services/schemes';
import * as appService from '../services/applications';
import AuthContext from '../context/AuthContext';
import LoadingSpinner from '../components/common/LoadingSpinner';
import './SchemeDetails.css';

const SchemeDetails = () => {
  const [scheme, setScheme] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const { id } = useParams();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchScheme = async () => {
      try {
        setLoading(true);
        const data = await schemeService.getSchemeById(id);
        setScheme(data);
      } catch (err) {
        setError(err.toString());
      } finally {
        setLoading(false);
      }
    };
    fetchScheme();
  }, [id]);

  const handleApply = async () => {
    if (!user) {
      navigate('/login');
      return;
    }
    
    // For a real app, you'd show a form to collect 'formData'
    // For this demo, we'll send dummy profile data
    const dummyFormData = {
      fullName: user.profile?.firstName || 'Demo User',
      email: user.email,
      age: user.profile?.age || 'N/A',
      state: user.profile?.state || 'N/A',
    };

    try {
      setError('');
      setMessage('');
      await appService.submitApplication(scheme._id, dummyFormData);
      setMessage('Application submitted successfully! Check "My Applications" for status.');
    } catch (err) {
      setError(err.toString());
    }
  };

  const renderApplyButton = () => {
    if (scheme.schemeType === 'Government') {
      return (
        <a href={scheme.officialLink} target="_blank" rel="noopener noreferrer" className="btn-apply government">
          Apply on Official Website
        </a>
      );
    }
    
    // Check if user is logged in
    if (!user) {
      return (
        <button onClick={() => navigate('/login')} className="btn-apply private">
          Login to Apply
        </button>
      );
    }

    // Check if user is a 'Citizen'
    if (user.role !== 'Citizen') {
      return null; // Don't show apply button for Admin/Coordinator
    }

    return (
      <button onClick={handleApply} className="btn-apply private">
        Apply Now
      </button>
    );
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <div className="scheme-error">Error: {error}</div>;
  if (!scheme) return <p>Scheme not found.</p>;

  return (
    <div className="scheme-details-container">
      <header className="scheme-header">
        <h1>{scheme.title}</h1>
        <p className="department">Department: {scheme.department}</p>
        <span className={`badge ${scheme.schemeType?.toLowerCase()}`}>{scheme.schemeType}</span>
      </header>
      
      {error && <div className="message error-message">{error}</div>}
      {message && <div className="message success-message">{message}</div>}

      <div className="scheme-content">
        <div className="scheme-main">
          <h3>Description</h3>
          <p>{scheme.description}</p>

          <h3>Benefits</h3>
          <ul>
            {scheme.benefits?.map((benefit, index) => (
              <li key={index}>{benefit}</li>
            ))}
          </ul>

          <h3>How to Apply</h3>
          <p>{scheme.howToApply || 'Follow the instructions on the official website.'}</p>
        </div>

        <aside className="scheme-sidebar">
          <div className="sidebar-card">
            <h3>Eligibility</h3>
            <ul>
              {scheme.eligibility?.ageMin && <li>Minimum Age: {scheme.eligibility.ageMin}</li>}
              {scheme.eligibility?.ageMax && <li>Maximum Age: {scheme.eligibility.ageMax}</li>}
              {scheme.eligibility?.state && <li>State: {scheme.eligibility.state}</li>}
              {scheme.eligibility?.gender && <li>Gender: {scheme.eligibility.gender}</li>}
            </ul>
          </div>
          <div className="sidebar-card">
            <h3>Documents Required</h3>
            <ul>
              {scheme.documentsRequired?.map((doc, index) => (
                <li key={index}>{doc}</li>
              ))}
            </ul>
          </div>
          <div className="apply-section">
            {renderApplyButton()}
          </div>
        </aside>
      </div>
    </div>
  );
};

export default SchemeDetails;
